import React, {useEffect, useState} from 'react';
import {StyleSheet, View, FlatList, Image, Text} from 'react-native';
import FontAwesome from 'react-native-vector-icons/FontAwesome';

export default function CheckIn() {
  const data = [
    {
      id: '1',
      name: 'User 1',
      comment:
        'Went out and visited the house this weekend. I am almost ready to put a bid on!',
      imageUrl:
        'data:image/jpeg;base64,',
    },
    {
      id: '2',
      name: 'User 2',
      comment:
        'Went out and visited the house this weekend. I am almost ready to put a bid on!',
      imageUrl: '',
    },
    {
      id: '3',
      name: 'User 3',
      comment:
        'Went out and visited the house this weekend. I am almost ready to put a bid on!',
      imageUrl:
        'data:image/jpeg;base64,',
    },
  ];
  const [currentDate, setCurrentDate] = useState(new Date());
  const formattedDate = `${getDayWithSuffix(
    currentDate.getDate(),
  )} of ${getMonthName(currentDate.getMonth())} ${currentDate.getFullYear()}`;
  function getDayWithSuffix(day) {
    if (day >= 11 && day <= 13) {
      return `${day}th`;
    }
    switch (day % 10) {
      case 1:
        return `${day}st`;
      case 2:
        return `${day}nd`;
      case 3:
        return `${day}rd`;
      default:
        return `${day}th`;
    }
  }
  function getMonthName(month) {
    const monthNames = [
      'January',
      'February',
      'March',
      'April',
      'May',
      'June',
      'July',
      'August',
      'September',
      'October',
      'November',
      'December',
    ];
    return monthNames[month];
  }
  useEffect(() => {
    setCurrentDate(new Date());
  }, []);

  return (
    <View style={styles.container}>
      <View style={styles.flatlistCon}>
        <FlatList
          data={data}
          keyExtractor={item => item.id}
          renderItem={({item}) => (
            <View style={styles.item}>
              {item.imageUrl && (
                <Image
                  source={{
                    uri: item.imageUrl,
                  }}
                  style={styles.image}
                  resizeMode="cover"
                />
              )}
              <View style={styles.userCon}>
                <View style={styles.iconCon}>
                  <FontAwesome name="user" size={25} color="black" />
                  {/* {console.log(formattedDate)} */}
                </View>
                <View style={styles.userDetailsCon}>
                  <Text style={styles.userName}>{item.name}</Text>
                  <Text style={styles.date}>{formattedDate}</Text>
                </View>
              </View>
              <View style={styles.commentCon}>
                <Text style={styles.comment}>{item.comment}</Text>
              </View>
            </View>
          )}
        />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f0f0f0',
  },
  flatlistCon: {
    marginTop: 10,
  },
  userCon: {
    flexDirection: 'row',
    marginTop: 15,
  },
  userName: {
    fontSize: 18,
    color: 'black',
  },
  date: {
    fontSize: 18,
    color: 'blue',
  },
  iconCon: {
    width: 50,
    height: 50,
    backgroundColor: '#f0f0f0',
    justifyContent: 'center',
    alignItems: 'center',
    borderRadius: 10,
  },
  userDetailsCon: {
    left: 8,
  },
  commentCon: {
    marginTop: 10,
    marginBottom: 10,
  },
  comment: {
    lineHeight: 20,
    // textAlign: 'justify',
  },
  item: {
    width: '90%',
    padding: 10,
    marginBottom: 10,
    marginTop: 10,
    borderBottomWidth: 1,
    borderBottomColor: '#ccc',
    alignSelf: 'center',
    backgroundColor: 'white',
    borderRadius: 10,
  },
  image: {
    height: 180,
    width: '100%',
    borderRadius: 10, // Adjust the width as needed
  },
});
